import { memo, useEffect, useState } from 'react';
import { Image, Space, Tag, message, Popconfirm } from 'antd';
import Tabs from '@/components/tabs';
import Table from '@/components/table';
import Modal from './modal';
import InfoModal from './infoModal';
import { getTopicList, deleteTopicList, updateTopicList } from '@/util/servers';
import { getDate } from '@/util/function';

const TopicList = memo(() => {
  const basicData = {
    title: '话题管理',
    tabs: [{ title: '话题列表', code: 'topic' }],
  };

  const [pages, setPages] = useState({
    pageNo: 1,
    pageSize: 10,
  });

  const [data, setData] = useState(undefined);
  const [total, setTotal] = useState(0);
  const [queryValue, setQueryValue] = useState('');
  const [current, setCurrent] = useState(1);
  const [modal, setModal] = useState(false);
  const [infoModal, setInfoModal] = useState(false);
  const [detail, setDetail] = useState();
  const [id, setId] = useState<number>();

  const columns = [
    {
      title: '内容',
      dataIndex: 'msg',
      key: 'msg',
      align: 'center' as 'center',
    },
    {
      title: '图片',
      dataIndex: 'picture',
      key: 'picture',
      align: 'center' as 'center',
      render: (t: any, record: any) => {
        return (
          <>
            {record.picture1 && (
              <Image
                width={60}
                height={60}
                src={`/ocean${record?.picture1}`}
                fallback="data:image/png;base64,"
              />
            )}
            &nbsp;
            {record.picture2 && (
              <Image
                width={60}
                height={60}
                src={`/ocean${record?.picture2}`}
                fallback="data:image/png;base64,"
              />
            )}
            &nbsp;
            {record.picture3 && (
              <Image
                width={60}
                height={60}
                src={`/ocean${record?.picture3}`}
                fallback="data:image/png;base64,"
              />
            )}
            &nbsp;
            {record.picture4 && (
              <Image
                width={60}
                height={60}
                src={`/ocean${record?.picture4}`}
                fallback="data:image/png;base64,"
              />
            )}
            &nbsp;
            {record.picture5 && (
              <Image
                width={60}
                height={60}
                src={`/ocean${record?.picture5}`}
                fallback="data:image/png;base64,"
              />
            )}
          </>
        );
      },
    },
    {
      title: '评论数',
      dataIndex: 'comment',
      key: 'comment',
      align: 'center' as 'center',
    },
    {
      title: '点赞数',
      key: 'praise',
      dataIndex: 'praise',
      align: 'center' as 'center',
    },
    {
      title: '发布人',
      key: 'userName',
      dataIndex: 'userName',
      align: 'center' as 'center',
    },
    {
      title: '发布时间',
      key: 'createTime',
      dataIndex: 'createTime',
      align: 'center' as 'center',
      render: (t: number) => {
        return getDate(t);
      },
    },
    {
      title: '状态',
      key: 'checkStatus',
      dataIndex: 'checkStatus',
      align: 'center' as 'center',
      render: (t: number) => {
        switch (t) {
          case 0:
            return <Tag color="orange">待审核</Tag>;
          case 1:
            return <Tag color="green">已通过</Tag>;
          case 2:
            return <Tag color="red">已拒绝</Tag>;
        }
      },
    },
    {
      title: '操作',
      key: 'handle',
      dataIndex: 'handle',
      align: 'center' as 'center',
      render: (text: any, record: any) => {
        function onDelete(id: number) {
          deleteTopicList({ id }).then((res) => {
            if (res.status === 200 && res?.data?.code === 0) {
              message.success('删除成功');
              reload();
            } else {
              message.error('删除失败');
            }
          });
        }

        function onAudit(id: number) {
          setModal(true);
          setId(id);
        }

        function onDetail(record: any) {
          setInfoModal(true);
          setDetail(record);
        }

        return (
          <Space size="middle">
            <Popconfirm
              title="是否确定删除?"
              onConfirm={() => onDelete(record.id)}
            >
              <a>删除</a>
            </Popconfirm>
            {record.checkStatus === 0 ? (
              <a onClick={() => onAudit(record.id)}>审核</a>
            ) : (
              <a onClick={() => onDetail(record)}>审核信息</a>
            )}
          </Space>
        );
      },
    },
  ];

  function reload() {
    setData(undefined);
    const options = Object.assign({}, pages, {
      params: { msg: queryValue },
    });
    getTopicList(options).then((res) => {
      if (res.status === 200) {
        setData(res?.data?.data?.records);
        setTotal(res?.data?.data?.total);
      }
    });
  }

  useEffect(() => {
    setData(undefined);
    const options = Object.assign({}, pages, { params: { msg: queryValue } });
    getTopicList(options).then((res) => {
      if (res.status === 200 && res?.data?.code === 0) {
        setData(res.data.data.records);
        setTotal(res.data.data.total);
      }
    });
  }, [pages, queryValue]);

  function onQuery(value: string) {
    setQueryValue(value);
    setPages({
      pageNo: 1,
      pageSize: 10,
    });
    setCurrent(1);
  }

  function onPageChange(page: number, pageSize: number) {
    setPages({
      pageNo: page,
      pageSize: pageSize,
    });
    setCurrent(page);
  }

  function onModalClose() {
    setModal(false);
  }

  async function onModalSubmit(values: {
    checkStatus: string;
    checkMsg: string;
  }) {
    const userData: string | null = window.localStorage.getItem('userData');
    const userJson = JSON.parse(userData as string);
    if (userJson && userJson.user) {
      const options = {
        id,
        checkStatus: values.checkStatus,
        checkMsg: values.checkMsg,
        checkName: userJson.user.username,
      };
      const data = await updateTopicList(options);
      if (data.status === 200 && data.data.code === 0) {
        message.success('审核完成');
        reload();
      } else {
        message.error('审核失败');
      }
    } else {
      message.warning('无法获取用户信息');
    }
  }

  function onInfoModalClose() {
    setInfoModal(false);
  }

  return (
    <div id="tableContent" style={{ background: '#f0f2f5' }}>
      <div style={{ height: '100%', padding: '33px 17px' }}>
        <Tabs title={basicData.title} tabs={basicData.tabs} activeKey={'topic'}>
          <Table
            columns={columns}
            dataSource={data}
            search
            current={current}
            total={total}
            onQuery={onQuery}
            onPageChange={onPageChange}
          />
          {modal && (
            <Modal
              visible={modal}
              title="社群审核"
              onClose={onModalClose}
              onSubmit={onModalSubmit}
            />
          )}
          <InfoModal
            visible={infoModal}
            title="社群信息"
            data={detail}
            onClose={onInfoModalClose}
          />
        </Tabs>
      </div>
    </div>
  );
});

export default TopicList;
