import { memo, useEffect, useState } from 'react';
import { Drawer, Descriptions, Divider, Button, Image } from 'antd';
import { getDate } from '@/util/function';
interface IProps {
  visible: boolean;
  data: any | null;
  onDetailClose: Function;
}

const Detail: React.FC<IProps> = memo(({ visible, data, onDetailClose }) => {
  const [putawayTime, setPutawayTime] = useState();
  const [createTime, setCreateTime] = useState<number>(0);
  const [data1, setData1] = useState<Array<any>>([]);
  const [data2, setData2] = useState<Array<any>>([]);
  const [data3, setData3] = useState<Array<any>>([]);

  useEffect(() => {
    if (data && data.length > 0) {
      const d1 = [];
      const d2 = [];
      const d3 = [];
      for (let i = 0; i < data.length; i += 1) {
        const d = data[i];
        if (d.foodType === 0) {
          d1.push(d);
        } else if (d.foodType === 1) {
          d2.push(d);
        } else {
          d3.push(d);
        }
      }
      setPutawayTime(data[0].putawayTime);
      setCreateTime(data[0].createTime);
      setData1(d1);
      setData2(d2);
      setData3(d3);
    }
  }, [data]);

  return (
    <Drawer
      visible={visible}
      closable={false}
      mask={false}
      placement="right"
      footer={<Button onClick={() => onDetailClose()}>关闭</Button>}
      footerStyle={{ textAlign: 'center' }}
      destroyOnClose={true}
      width={500}
    >
      <Divider orientation="left">基本信息</Divider>
      <Descriptions column={2}>
        <Descriptions.Item label="食谱日期" span={2}>
          {putawayTime}
        </Descriptions.Item>
        <Descriptions.Item label="添加时间" span={2}>
          {getDate(createTime)}
        </Descriptions.Item>
      </Descriptions>
      {data1.length > 0 && <Divider orientation="left">早餐</Divider>}
      {data1.map((res) => (
        <>
          <Descriptions column={2}>
            <Descriptions.Item label="名称" span={2}>
              {res.foodName}
            </Descriptions.Item>
            <Descriptions.Item label="图片" span={2}>
              {res.foodPicture && (
                <Descriptions.Item span={1}>
                  <Image
                    width={150}
                    height={150}
                    src={`/ocean${res.foodPicture}`}
                    fallback="data:image/png;base64,"
                  />
                </Descriptions.Item>
              )}
              {res.foodPicture1 && (
                <Descriptions.Item span={1}>
                  <Image
                    width={150}
                    height={150}
                    src={`/ocean${res.foodPicture1}`}
                    fallback="data:image/png;base64,"
                  />
                </Descriptions.Item>
              )}
              {res.foodPicture2 && (
                <Descriptions.Item span={1}>
                  <Image
                    width={150}
                    height={150}
                    src={`.${res.foodPicture2}`}
                    fallback="data:image/png;base64,"
                  />
                </Descriptions.Item>
              )}
            </Descriptions.Item>
            <Descriptions.Item label="食材名称" span={2}>
              {res.foodMaterials}
            </Descriptions.Item>
            <Descriptions.Item label="烹饪方式" span={2}>
              <div dangerouslySetInnerHTML={{ __html: res?.cookWay }}></div>
            </Descriptions.Item>
          </Descriptions>
        </>
      ))}
      {data2.length > 0 && <Divider orientation="left">午餐</Divider>}
      {data2.map((res) => (
        <>
          <Descriptions column={2}>
            <Descriptions.Item label="名称" span={2}>
              {res.foodName}
            </Descriptions.Item>
            <Descriptions.Item label="图片" span={2}>
              {res.foodPicture && (
                <Descriptions.Item span={1}>
                  <Image
                    width={150}
                    height={150}
                    src={`/ocean${res.foodPicture}`}
                    fallback="data:image/png;base64,"
                  />
                </Descriptions.Item>
              )}
              {res.foodPicture1 && (
                <Descriptions.Item span={1}>
                  <Image
                    width={150}
                    height={150}
                    src={`/ocean${res.foodPicture1}`}
                    fallback="data:image/png;base64,"
                  />
                </Descriptions.Item>
              )}
              {res.foodPicture2 && (
                <Descriptions.Item span={1}>
                  <Image
                    width={150}
                    height={150}
                    src={`.${res.foodPicture2}`}
                    fallback="data:image/png;base64,"
                  />
                </Descriptions.Item>
              )}
            </Descriptions.Item>
            <Descriptions.Item label="食材名称" span={2}>
              {res.foodMaterials}
            </Descriptions.Item>
            <Descriptions.Item label="烹饪方式" span={2}>
              <div dangerouslySetInnerHTML={{ __html: res?.cookWay }}></div>
            </Descriptions.Item>
          </Descriptions>
        </>
      ))}
      {data3.length > 0 && <Divider orientation="left">晚餐</Divider>}
      {data3.map((res) => (
        <>
          <Descriptions column={2}>
            <Descriptions.Item label="名称" span={2}>
              {res.foodName}
            </Descriptions.Item>
            <Descriptions.Item label="图片" span={2}>
              {res.foodPicture && (
                <Descriptions.Item span={1}>
                  <Image
                    width={150}
                    height={150}
                    src={`/ocean${res.foodPicture}`}
                    fallback="data:image/png;base64,"
                  />
                </Descriptions.Item>
              )}
              {res.foodPicture1 && (
                <Descriptions.Item span={1}>
                  <Image
                    width={150}
                    height={150}
                    src={`/ocean${res.foodPicture1}`}
                    fallback="data:image/png;base64,"
                  />
                </Descriptions.Item>
              )}
              {res.foodPicture2 && (
                <Descriptions.Item span={1}>
                  <Image
                    width={150}
                    height={150}
                    src={`.${res.foodPicture2}`}
                    fallback="data:image/png;base64,"
                  />
                </Descriptions.Item>
              )}
            </Descriptions.Item>
            <Descriptions.Item label="食材名称" span={2}>
              {res.foodMaterials}
            </Descriptions.Item>
            <Descriptions.Item label="烹饪方式" span={2}>
              <div dangerouslySetInnerHTML={{ __html: res?.cookWay }}></div>
            </Descriptions.Item>
          </Descriptions>
        </>
      ))}
    </Drawer>
  );
});

export default Detail;
